function [ imageFiltree ] = appliquerFiltreFrequentiel( image, masque )% Cette fonction applique un filtre fréquentiel, puis retourne l'image% filtrée.% le masque doit être de la bonne tailleif ~isequal(size(masque),size(image))    error('L''image et le masque doivent avoir la même taille.')end% On traite l'image en doubleif isequal(class(image),'uint8')    image = im2double(image);end% Calcul et centrage du spectre de l'imageFc = fftshift( fft2(double(image)) );% Multiplication point par point du spectre et du masqueFc1 = Fc.*masque;% Decentrage du spectreF1 = ifftshift(Fc1);% Calcul de la transformée inverseimageFiltree = real( ifft2(F1) );% Affichage% figure(1)% imshow(image);% title('Avant le traitement frequentiel');% figure(2)% imageFiltree = uint8(imageFiltree*255);% imshow(imageFiltree);% title(' Après le traitement frequentiel');