function [S1] = afficherMagnitudeFourier( M )% ce script affiche la magnitude de la transformée de Fourier de l'image% spécifiée%% exemple:% afficherMagnitudeFourier(imread('lena.jpg'));% Calcul de la transformée de fourier de l'imageF = fft2( double(M) );% Centrage du spectreFc = fftshift(F);% Calcul de la magnitude pour l'affichage du spectreS = log(1+abs(Fc));% Affichage de la magnitude du spectreimshow(S,[]);title('Magnitude de la transformée de Fourier');